                    <span>{{like_new}}</span>
                </span>
            </div>
            <van-divider :style="{ borderColor: '#1989fa'}"/>
            <deletes :keys='list.Identification' :id="id" tag='artcle' :alllist='img_list'></deletes>
            <comment :id='id' tag='art' />
        </div>
    </div>
</template>
<script>
import re from '../tool/return.vue';
import comment from '../tool/comment.vue';
import deletes from '../tool/delete.vue';
import { ImagePreview,Toast } from 'vant';
export default {
    data() {
        return {
            id:this.$route.params.id,
            flag:false,
            list:{},
            img_list:[],
            like_old:0,
            like_new:0,
        }
    },
    created() {
        this.getart()
    },
    beforeDestroy() {
        this.getlike()
    },
    methods:{
        getart(){
            this.$axios({
                 method:'get',
                url:`${this.$store.state.url}artcle/artinfo/${this.id}`,
            }).then((reslut)=>{
                if(reslut.data.code == 0){
                    this.list = reslut.data.msg[0];
                    this.like_new = reslut.data.msg[0].likes;
                    this.like_old = reslut.data.msg[0].likes;
                    this.$axios({
                        method:'get',
                        url:`${this.$store.state.url}artcle/artinfoimg/${this.list.Identification}`,
                    }).then((res)=>{
                        if(res.data.code == 0){
                            this.img_list = res.data.msg;
                        }else if(res.data.code == 1){
                            Toast({
                                message: '访问数据时,我们迷路了',
                                icon: 'close'
                            });
                        }
                    })
                }else if(reslut.data.code == 1){
                    Toast({
                        message: '访问数据时,我们迷路了',
                        icon: 'close'
                    });
                }
            }).catch((err)=>{
                 Toast({
                    message: '访问数据时,它已经远走高飞了',
                    icon: 'question-o'
                });
            })
        },
        like(){
             if(this.flag == true){
                this.like_new --;
                this.flag = false;
            }else{
                this.like_new ++;
                this.flag = true;
                //发生请求
            }
        },
        getlike(){
            if (this.flag == false) return;
            if (this.$store.state.user == '') {
                return Toast({
                    message: '未登录不可点赞    Do not log in to thumb up',
                    icon: 'close'
                });
            }else{
                if (this.like_new == this.like_old) {
                    return
                }
                this.$axios({
                    method:'get',
                    url:`${this.$store.state.url}subsidiary/like`,
                    params:{
                        tag:'artcle',
                        id:this.id,
                        num:this.like_new,
                    }
                }).then((res)=>{
                    if(res.data.code == 0){
                    }else if(res.data.code == 1){
                        Toast({
                            message: '点赞失败  Failed to like',
                            icon: 'close'
                        });
                    }
                }).catch((err)=>{
                    Toast({
                        message: '服务器故障 Server down',
                        icon: 'question-o'
                    });
                })
            }
        },
        preview(){
            let opt = [];
            for (let i = 0; i < this.img_list.length; i++) {
                opt[i] = this.img_list[i].img_url;
            }
            ImagePreview(opt);
        },
        
    },
    components:{
        re,
        comment,
        deletes,
    }
}
</script>
