                    </van-field>
                <div class="mui-card-footer">
                    <router-link v-for="(item, index) in list" :key="index" :to="'/main/cartoon/info?url1='+item.url" tag="span">{{item.num}}</router-link>
                    <span @click="getmore">加载更多</span>
                </div>
            </div>
        </van-pull-refresh>
    </div>
</template>
<script>
import { Toast } from 'vant';
import hea from '../tool/return.vue';
export default {
    created() {
        this.getinfo();
        this.iffrom();
    },
    beforeDestroy() {
        this.add()
    },
    data() {
        return {
            url:this.$route.query.url,
            tag:this.$route.query.tag,
            info:{},
            more_list:[],
            list:[],
            newNum:0,
            oldNum:0,
            isLoading: false,
            show:true,
            digit:null,
            text:'加入漫天阁',
            isadd:false,
            ismain:true,
            sart:'立即阅读',
        }
    },
    methods: {
        getinfo(){
            this.$axios.get('http://api.pingcc.cn/?mhurl1=' + this.url).then((reslut)=>{
                if(reslut.data.code == 0){
                    this.info = reslut.data .data;
                    this.more_list = reslut.data.list;
                    this.getmore();
                    this.show = false;
                }else if(reslut.data.code == 1){
                    Toast({
                        message: '漫漫在来的路上迷路了(/▽＼)',
                        icon: 'close'
                    });
                    this.show = false;
                }
            }).catch((err)=>{
                 Toast({
                    message: '漫漫在来的路上被拐跑了。゜゜(´Ｏ`) ゜゜。',
                    icon: 'question-o'
                });
                this.show = false;
            });
        },
        getmore(){
            this.oldNum = this.newNum
            this.newNum += 11;
            if (this.list<11) {
                return this.list = this.more_list;
            }
            for (let i = this.oldNum; i < this.newNum; i++) {
                 if (i == this.zong) {
                    Toast({
                    message: '已经没有了啊～',
                    icon: 'flag-o'
                    });
                }
                this.$set(this.list,i,this.more_list[i])
            }
            this.show = false;
        },
        onRefresh() {
            setTimeout(() => {
                this.getinfo()
                this.isLoading = false;
            }, 400);
        },
        goto(){
            let url = parseInt(this.digit)-1;
            if(url > this.more_list.length){
                return  Toast({
                    message: '不存在此章节',
                    icon: 'flag-o'
                });
            }
            this.$router.push(`/main/cartoon/info?url1=${this.more_list[url].url}&name=${this.info.name}&shelf=${this.isadd}`)
        },
        iffrom(){
            if (this.tag == 'main') {
                this.sart = '立即阅读';
                return
            }else if (this.tag == 'user') {
                this.sart = '继续阅读';
                this.ismain = false;
                this.changeadd()
            }
        },
        changeadd(){
            if (this.$store.state.user == '') {
                 return Toast({
                    message: '未登录不可管理漫天阁',
                    icon: 'close'
                });
            }
            this.isadd = !this.isadd;
            this.text = this.isadd==false?'加入漫天阁':'移出漫天阁'
        },
        add(){
            /*
                name
                author
                cover
                time
                url
                email
            */
           if (this.ismain == true) {
                if(this.isadd == false) return;
                this.$axios({
                    method:'get',
                    url:`${this.$store.state.url}user/addcart`,
                    params:{
                        name:this.info.name,
                        author:this.info.author,
                        cover:this.info.cover,
                        time:this.info.time,
                        url:this.url,
                        email:JSON.parse(this.$store.state.user).email,
                    }
                }).then((reslut)=>{
                    if(reslut.data.code == 0){
                        Toast({
                            message: '漫天阁加入成功    All over the sky to join the success',
                            icon: 'passed'
                        });
                    }else if(reslut.data.code == 1){
                        Toast({
                            message: '漫天阁加入失败    The sky to join failure',
                            icon: 'close'
                        });
                        this.show = false;
                    }else if(reslut.data.code == 2){
                        Toast({
                            message: '漫画已存在    Comics already exist',
                            icon: 'close'
                        });
                        this.show = false;
                    }
                }).catch((err)=>{
                    Toast({
                        message: '漫天阁水灾    Flood in the sky',
                        icon: 'question-o'
                    });
                    this.show = false;
                });
           }else{
                if(this.isadd == true) return;
                this.$axios({
                    method:'get',
                    url:`${this.$store.state.url}user/delcart`,
                    params:{
                        name:this.info.name,
                        email:JSON.parse(this.$store.state.user).email,
                    }
                }).then((reslut)=>{
                    if(reslut.data.code == 0){
                        Toast({
                            message: '撤销漫画成功  Comic strip removed successfully',
                            icon: 'passed'
                        });
                    }else if(reslut.data.code == 1){
                        Toast({
                            message: '撤销漫画失败  Failed to cancel cartoon',
                            icon: 'close'
                        });
                        this.show = false;
                    }
                }).catch((err)=>{
                    Toast({
                        message: '漫天阁水灾    Flood in the sky',
                        icon: 'question-o'
                    });
                    this.show = false;
                });
           }
            
        },
        read(){
            if (this.isadd == true) {
                if (this.ismain == true) {
                    //main 点击加入书架后，第一次阅读
                    this.$router.push(`/main/cartoon/info?url1=${this.list[0].url}&name=${this.info.name}&shelf=true`)
                }else{
                    this.$axios({
                        method:'get',
                        url:`${this.$store.state.url}user/getcart`,
                        params:{
                            name:this.info.name,
                            email:JSON.parse(this.$store.state.user).email,
                        }
                    }).then((reslut)=>{
                        if(reslut.data.code == 0){
                            if (reslut.data.msg[0].last == null) {
                                //用户点击加入书架后，没有阅读，导致数据库为空
                                this.$router.push(`/main/cartoon/info?url1=${this.list[0].url}&name=${this.info.name}&shelf=true`)
                                
                            }else{
                                this.$router.push(`/main/cartoon/info?url1=${reslut.data.msg[0].last}&name=${this.info.name}&shelf=true`)
                            }
                        }else if(reslut.data.code == 1){
                            Toast({
                                message: '获取漫画失败  Failed to get comics',
                                icon: 'close'
                            });
                            this.show = false;
                        }
                    }).catch((err)=>{
                        Toast({
                            message: '漫天阁水灾    Flood in the sky',
                            icon: 'question-o'
                        });
                        this.show = false;
                    });
                }
            }else{
                this.$router.push(`/main/cartoon/info?url1=${this.list[0].url}&shelf=false`)
            }
          
        },
    },
    components:{
        hea
    }
}
</script>
