        </van-overlay>
        <van-pull-refresh v-model="isLoading" @refresh="onRefresh" :head-height='100' pulling-text='你拉我下试试(„ಡωಡ„)'  loosing-text='错了错了，不要拉了X﹏X' >
            <!-- 搜索栏和小图标 -->
            <hea></hea>
            <!-- 首图 -->
            <div class="shou">
                <van-grid :column-num='1' center>
                        <van-image :src="src"  cover lazy-load />
                </van-grid>
            </div>
            <!-- Grid 宫格 -->
            <van-grid :column-num="3" :gutter="10" :border='false'>
                <van-grid-item>
                    <strong slot="icon">图片</strong>
                    <small slot="text">Image</small>
                </van-grid-item>
                <van-grid-item>
                    <strong slot="icon">视频</strong>
                    <small slot="text">Video</small>
                </van-grid-item>
                <van-grid-item>
                    <strong slot="icon">文章</strong>
                    <small slot="text">Artcle</small>
                </van-grid-item>
                <van-grid-item>
                    <strong slot="icon">小说</strong>
                    <small slot="text">Fiction</small>
                </van-grid-item>
                <van-grid-item>
                    <strong slot="icon">漫画</strong>
                    <small slot="text">Novel</small>
                </van-grid-item>
                <van-grid-item>
                    <strong slot="icon">影视</strong>
                    <small slot="text">Novel</small>
                </van-grid-item>
            </van-grid>
            <!-- list -->
            <van-divider content-position="left" :style="{color:'#999'}">Iamge</van-divider>
            <water :data='img_list' tag='image'></water>
            <van-divider content-position="left" :style="{color:'#999'}">Video</van-divider>
            <water :data='mv_list' tag='video'></water>

        </van-pull-refresh>
    </div>
</template>
<script>
import hea from '../tool/head';
import water from '../tool/waterfall';
import { Toast } from 'vant';
export default {
    data() {
        return {
            img_list:[],
            mv_list:[],
            show:true,
            isLoading: false,
            src:'',
        }
    },
created() {
    this.aa();
    this.getimg();
},
    methods: {
        getimg(){
            if (this.$store.state.homeimgs=='') {
                this.$axios.get(`${this.$store.state.url}subsidiary/homeimg`).then((reslut)=>{
                    if(reslut.data.code == 0){
                        this.src = reslut.data.msg[0].img;
                        this.$store.commit('changeHomeimgs',JSON.stringify(reslut.data.msg[0].img))
                    }else if(reslut.data.code == 1){
                        this.show = false;
                        Toast({
                            message: '获取首页图失败',
                            icon: 'close'
                        });
                    }
                }).catch((err)=>{
                    this.show = false;
                    Toast({
                        message: '首页图服务器故障',
                        icon: 'question-o'
                    });
                })
            }else{
                this.src = JSON.parse(localStorage.getItem('homeimgs'));
            }
        },
        aa(){
            // 获取首页图片
            if (this.$store.state.homeimg=='') {
                this.$axios.get(`${this.$store.state.url}image/getimages/0/10/likes/ASC`).then((reslut)=>{
                    if(reslut.data.code == 0){
                        this.$store.commit('changeHomeimg',JSON.stringify(reslut.data.msg))
                        this.img_list = reslut.data.msg
                    }else if(reslut.data.code == 1){
                        this.show = false;
                        Toast({
                            message: '访问数据时,我们迷路了',
                            icon: 'close'
                        });
                    }
                }).catch((err)=>{
                    this.show = false;
                    Toast({
                        message: '访问数据时,它已经远走高飞了',
                        icon: 'question-o'
                    });
                })
            }else{
                this.img_list = JSON.parse(localStorage.getItem('homeimg'));
            }
      
            //获取首页视频
            if (this.$store.state.homemv=='') {
                this.$axios.get(`${this.$store.state.url}video/getvideos/0/10/likes/ASC`).then((reslut)=>{
                    if(reslut.data.code == 0){
                        this.$store.commit('changeHomemv',JSON.stringify(reslut.data.msg))
                        this.mv_list = reslut.data.msg
                    }else if(reslut.data.code == 1){
                        this.show = false;
                        Toast({
                            message: '访问数据时,我们迷路了',
                            icon: 'close'
                        });
                    }
                }).catch((err)=>{
                    this.show = false;
                    Toast({
                        message: '访问数据时,它已经远走高飞了',
                        icon: 'question-o'
                    });
                })
            }else{
                this.mv_list = JSON.parse(localStorage.getItem('homemv'));
            }
            this.show = false;
        },
        msgs(){
            this.$axios.get(`${this.$store.state.url}image/getimages/0/10/likes/ASC`).then((reslut)=>{
                if(reslut.data.code == 0){
                    this.$store.commit('changeHomeimg',JSON.stringify(reslut.data.msg))
                    this.img_list = reslut.data.msg
                }else if(reslut.data.code == 1){
                    this.show = false;
                    Toast({
                        message: '访问数据时,我们迷路了',
                        icon: 'close'
                    });
                }
            }).catch((err)=>{
                this.show = false;
                Toast({
                    message: '访问数据时,它已经远走高飞了',
                    icon: 'question-o'
                });
            })

            this.$axios.get(`${this.$store.state.url}video/getvideos/0/10/likes/ASC`).then((reslut)=>{
                if(reslut.data.code == 0){
                    this.$store.commit('changeHomemv',JSON.stringify(reslut.data.msg))
                    this.mv_list = reslut.data.msg
                }else if(reslut.data.code == 1){
                    this.show = false;
                    Toast({
                        message: '访问数据时,我们迷路了',
                        icon: 'close'
                    });
                }
            }).catch((err)=>{
                this.show = false;
                Toast({
                    message: '访问数据时,它已经远走高飞了',
                    icon: 'question-o'
                });
            })
        },
        onRefresh() {
            setTimeout(() => {
                this.msgs();
                this.isLoading = false;
            }, 400);
        }
    },
    components:{
        water,
        hea
    }

}
</script>
